create table DEPARTMENT
(
    DEPARTMENTID CHAR(10)
        constraint DEPARTMENT_PK
            primary key,
    BUILDING     VARCHAR2(35 char)  ,
    DESCRIPTION  VARCHAR2(255 char) ,
    DUTIES       VARCHAR2(255 char)
)
/

create table EMPLOYEE
(
    EMPLOYEEID              CHAR(10)
        constraint EMPLOYEE_PK
            primary key,
    FIRSTNAME               VARCHAR2(50)      ,
    LASTNAME                VARCHAR2(50 char) ,
    HIREDATE                DATE              ,
    PHONENUMBER             VARCHAR2(10 char),
    ADDRESS                 VARCHAR2(35 char),
    DEPARTMENT_DEPARTMENTID CHAR(10)
        constraint EMPLOYEE_DEPARTMENT_FK
            references DEPARTMENT,
    SALARY                  NUMBER(9, 2),
    BONUS                   NUMBER(9, 2),
    RAISE                   NUMBER(2, 2)
)
/

create table PURCHASE_ORDER
(
    ORDERID             CHAR(10)
        constraint PURCHASE_ORDER_PK
            primary key,
    "Date"              DATE,
    DELIVERYDATE        DATE     ,
    EMPLOYEE_EMPLOYEEID CHAR(10)
        constraint PURCHASE_ORDER_EMPLOYEE_FK
            references EMPLOYEE
)
/


create table INVOICE
(
    INVOICEID              CHAR(10)
        constraint INVOICE_PK
            primary key,
    ISSUEDATE              DATE               NOT NULL, --all invoices have to be issued sometime, dont make a null invoice
    DUEDATE                DATE               ,
    SUBJECT                VARCHAR2(255 char) NOT NULL, --all invoices have to be due sometime, usually a few months ahead
    PURCHASE_ORDER_ORDERID CHAR(10)
        constraint INVOICE_PURCHASE_ORDER_FK
            references PURCHASE_ORDER
)
/

create table INVOICELINE
(
    INVOICE_INVOICEID CHAR(10)
        constraint INVOICELINE_INVOICE_FK
            references INVOICE,
    INVOICELINEID     CHAR(10) ,
    ITEMTYPE          VARCHAR2(30),
    DESCRIPTION       VARCHAR2(255 char),
    QUANTITY          NUMBER   ,
    UNITPRICE         NUMBER   ,
    TAX               NUMBER   ,
    constraint INVOICELINE_PK
        primary key (INVOICELINEID, INVOICE_INVOICEID)
)
/


create table ACCOUNT
(
    ACCOUNTID            CHAR(10)
        constraint ACCOUNT_PK
            primary key,
    ACCOUNTNAME          VARCHAR2(50 char) ,
    ACCOUNTNUMBER        NUMBER(10)        NOT NULL, -- we must be able to do something with the accounts if they are in the database,
    ACCOUNTROUTINGNUMBER NUMBER(5)         ,
    ACCOUNTDESCRIPTION   VARCHAR2(255 char)
)
/

create table PAYMENT
(
    ACCOUNT_ACCOUNTID CHAR(10)
        constraint PAYMENT_ACCOUNT_FK
            references ACCOUNT,
    PAYMENTID         CHAR(10)
        constraint PAYMENT1_PK
            primary key,
    METHOD            VARCHAR2(255 char) ,
    PAYMENTDATE       DATE               ,
    PAYMENTRECIVETIME DATE               ,
    PAYMENTAMOUNT     NUMBER,
    INVOICE_INVOICEID CHAR(10)
        constraint PAYMENT_INVOICE_FK
            references INVOICE
)
/




create table SHIPMENT
(
    SHIPMENTID             CHAR(10)
        constraint SHIPMENT_PK
            primary key,
    "Date"                 DATE,
    PURCHASE_ORDER_ORDERID CHAR(10)
        constraint SHIPMENT_PURCHASE_ORDER_FK
            references PURCHASE_ORDER,
    SUPPLIER               VARCHAR2(35 char),
    FULFILMENTMETHOD       VARCHAR2(35 char),
    DROPLOCATION           VARCHAR2(35 char),
    PRICEPERPOUND          NUMBER,
    WEIGHT                 NUMBER
)
/

create table RECEVING_RECIEPT
(
    RECEVINGRECIEPTID   CHAR(10)
        constraint RECEVING_RECIEPT_PK
            primary key,
    SHIPMENT_SHIPMENTID CHAR(10)
        constraint RECEVING_RECIEPT_SHIPMENT_FK
            references SHIPMENT,
    EMPLOYEE_EMPLOYEEID CHAR(10)
        constraint RECEVING_RECIEPT_EMPLOYEE_FK
            references EMPLOYEE,
    SHIPPINGDOCK        VARCHAR2(35 char),
    DATERECIVED         DATE
)
/

create table RECEVING_REPORT_LINE
(
    RECEVINGREPORTLINEID CHAR(10) ,
    RECEVINGRECIEPTID    CHAR(10)
        constraint RRL_RR_FK
            references RECEVING_RECIEPT,
    ITEMDESCRIPTION      VARCHAR2(255 char),
    ITEMQUANTITY         NUMBER,
    ITEMPRICE            NUMBER,
    constraint RECEVING_REPORT_LINE_PK
        primary key (RECEVINGREPORTLINEID, RECEVINGRECIEPTID)
)
/

create table INVENTORY_ITEM
(
    ITEMID         CHAR(10)
        constraint INVENTORY_ITEM_PK
            primary key,
    DESCRIPTION    VARCHAR2(250 char),
    QUANTITY       NUMBER,
    HEIGHTINCHES   NUMBER,
    WIDTHINCHES    NUMBER,
    LENGTHINCHES   NUMBER,
    WEIGHTPOUND    NUMBER,
    MANUFACTURERER VARCHAR2(30 char)
)
/


create table PURCHASE_ORDER_LINE
(
    PURCHASE_ORDER_ORDERID CHAR(10)
        constraint POL_PO_FK
            references PURCHASE_ORDER,
    ORDERLINEID            CHAR(10) ,
    ESITMATEDITEMPRICE     NUMBER,
    ITEMQUANTITY           NUMBER,
    INVENTORY_ITEM_ITEMID  CHAR(10)
        constraint POL_INVITEM_FK
            references INVENTORY_ITEM,
    constraint PURCHASE_ORDER_LINE_PK
        primary key (PURCHASE_ORDER_ORDERID, ORDERLINEID)
)
/

create table WAREHOUSE
(
    WAREHOUSEID     CHAR(8)
        constraint WAREHOUSE_PK
            primary key,
    STREETADDRESS   VARCHAR2(95 char) NOT NULL,
    STATE           VARCHAR2(2 char),
    CITY            VARCHAR2(35 char),
    ZIPCODE         VARCHAR2(35 char),
    NAME            VARCHAR2(255 char),
    DATEESTABLISHED DATE NOT NULL
)
/

create table STOCKPILE
(
    INVENTORY_ITEM_ITEMID  CHAR(10)
        constraint STOCKPILE_INVENTORY_ITEM_FK
            references INVENTORY_ITEM,
    WAREHOUSE_WAREHOUSEID  CHAR(8)
        constraint STOCKPILE_WAREHOUSE_FK
            references WAREHOUSE,
    LASTINVENTORYCHECKDATE DATE     ,
    QUANTITY               NUMBER,
    constraint STOCKPILE_PK
        primary key (INVENTORY_ITEM_ITEMID, WAREHOUSE_WAREHOUSEID, LASTINVENTORYCHECKDATE)
)
/


